mapper.select:

SELECT bno, title, content, writer, cdate, view_cnt
FROM (
         SELECT rownum AS rn, bno, title, content, writer, cdate, view_cnt
         FROM (
                  SELECT bno, title, content, writer, cdate, view_cnt
                  FROM board
                  ORDER BY bno desc
              )
         WHERE rownum <= #{page} * #{pageSize}
    )
WHERE rn > (#{page} - 1) * #{pageSize}
